import React, { useState, useEffect } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Alert from 'react-bootstrap/Alert';


const DeleteMovie = (props) => {
  const [movie, setMovie] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);

  const onChange = evt => {
    setMovie(evt.target.value);
  }

  const onClick = evt => {
    evt.preventDefault();
    if (movie === "") {
      setError("You must choose a movie!");
    } else {
      setError("");
      props.facade.addMovie(movie)
        .then(res => {
          if (res.hasOwnProperty("id")) setSuccess(true);
        })
        .catch(err => console.log(err));
    }
  }



  return (
    <Form onChange={onChange}>
      <h5>Delete movie</h5>



      {
        error !== "" ? (
          <Alert variant="danger">{error}</Alert>
        ) : null
      }

      <Form.Group controlId="year">
        <Form.Label>What movie would you like to delete?</Form.Label>
        <Form.Control as="select">
          <option>Select a year</option>
          {
            props.allMovies.map((movie, i) => (
              <option key={i} value={movie.id}>{movie.title}</option>
            ))
          }
        </Form.Control>
      </Form.Group>

      <Button onClick={onClick}>Add movie</Button>
      {
        success ? (
          <Alert variant="success" style={{ marginTop: 10 }}>The movies has been added!</Alert>
        ) : null
      }



    </Form>

  );
}

export default DeleteMovie;
