import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Switch, NavLink, Redirect } from 'react-router-dom';
import "./App.css";
import facade from "./apiFacade";
import LogIn from "./components/LogIn";

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Button from "react-bootstrap/Button";
import ListGroup from "react-bootstrap/ListGroup";
import Alert from "react-bootstrap/Alert";


function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  const login = (user, pass) => {
    facade
      .login(user, pass)
      .then(res => setLoggedIn(true))
      .catch(err => console.log("wrong credentials"));
  };
  const logout = () => {
    facade.logout();
    setLoggedIn(false);
  };
  return (
    <div>
      <Router>
        <Header loggedIn={loggedIn} logout={logout} facade={facade} />
        <Switch>
          <Route exact path="/">
            <Home loggedIn={loggedIn} facade={facade} />
          </Route>
          <Route path="/login">
            {!loggedIn ? (
              <LogIn login={login} />
            ) : (
                <Redirect to="/" />
              )
            }
          </Route>
        </Switch>
      </Router>

    </div>
  );
}

const Header = (props) => {
  console.log(props.facade.getRole())
  return (
    <ul className="menu">
      <li><span className="logo">MovieRev</span></li>
      <li><NavLink to="/" exact activeClassName="active">Home</NavLink></li>
      {
        props.loggedIn && props.facade.getRole() === "admin" ? (
          <li><NavLink to="/admin" activeClassName="active">Admin</NavLink></li>
        ) : null
      }

      {
        props.loggedIn ? (
          <li><NavLink to="/login" activeClassName="active" onClick={props.logout}>Logout</NavLink></li>
        ) : (
            <li><NavLink to="/login" activeClassName="active">Login</NavLink></li>

          )
      }
    </ul>
  );
}

const Home = (props) => {
  const [search, setSearch] = useState({ "query": "", "searchBy": "" });
  const [searchResults, setSearchResults] = useState([1]);
  const onChange = evt => {
    const target = evt.target;
    if (target.tagName === "SELECT") {
      search['searchBy'] = target.value;
      setSearch({ ...search });
      switch (target.value) {
        case "getbytitle":
          document.getElementById("searchInput").placeholder = "Eg. Bad Boys For Life";
          break;

        case "getbydirector":
          document.getElementById("searchInput").placeholder = "Eg. Will Smith";
          break;

        case "getbyactor":
          document.getElementById("searchInput").placeholder = "Eg. Martin Lawrence";
          break;

        case "getbygenre":
          document.getElementById("searchInput").placeholder = "Eg. Action";
          break;
        default:
          break;
      }
    } else if (target.tagName === "INPUT") {
      search['query'] = target.value;
      setSearch({ ...search });
    }
  }

  const onClick = evt => {
    evt.preventDefault();
    if (search.query === "" || search.searchBy === "") {
      document.getElementById("search-results").innerText = "You may not leave any fields empty.";
    } else {
      facade.fetchMovies(search)
        .then(res => console.log(res))
        .catch(err => console.log(err));
    }
  }

  return (
    <Container>
      <Row className="d-flex justify-content-center">

        <Col className="d-flex default-box" style={{ flexDirection: "column" }} md="7">
          <div className="box-header">Home</div>
          <div>
            <p>
              Feel free to search for any movie by movie title, director, actor or genre.
            </p>
            <Form onChange={onChange}>
              <InputGroup className="mb-3">

                <Form.Control
                  id="searchInput"
                  placeholder="Please choose what you want to search by"
                  aria-label="search-input"
                />
                <InputGroup.Append>
                  <Form.Control as="select" style={{ borderTopLeftRadius: "0", borderBottomLeftRadius: "0" }}>
                    <option value="">Please choose</option>
                    <option value="getbytitle">Title</option>
                    <option value="getbydirector">Director</option>
                    <option value="getbyactor">Actor</option>
                    <option value="getbygenre">Genre</option>
                  </Form.Control>
                </InputGroup.Append>
              </InputGroup>
              <Button onClick={onClick}>Search for movie</Button>
            </Form>
          </div>
          <div className="search-results-box" id="search-results">
            {
              searchResults.length > 0 ? (
                <ul className="movie-list">
                  {
                    searchResults.map((movie, i) => (
                      <li key={i} className="movie-box">
                        <div>
                          <label>Title:</label>{movie.title}
                        </div>

                        <div>
                          <label>Year:</label>{movie.year}
                        </div>

                        <div>
                          <label>Directors:</label>{
                            movie.directors.map((director, j) => (
                              <span key={j}> {director.name},</span>
                            ))
                          }
                        </div>

                        <div>
                          <label>Actors:</label>{
                            movie.actors.map((actor, ij) => (
                              <span key={ij}> {actor.name},</span>
                            ))
                          }
                        </div>

                        <div>
                          <label>Genres:</label>{
                            movie.genres.map((genre, iji) => (
                              <span key={iji}> {genre.name},</span>
                            ))
                          }
                        </div>

                        <div className="votes">
                          <Button>Thumbs down</Button>
                          <Alert variant="light" style={{ marginBottom: 0 }}>{movie.votes}</Alert>
                          <Button>Thumbs up</Button>
                        </div>
                      </li>
                    ))
                  }
                </ul>

                /*

                  <li className="movie-box">


                    <div>
                      <label>Year:</label>2020
                                  </div>
                    <div>
                      <label>Director:</label>Will Smith
                                  </div>
                    <div>
                      <label>Genre:</label>Action, Comedy
                                  </div>
                    <div>
                      <label>Actors:</label>
                      <ul style={{ listStyle: "none", paddingLeft: 0 }}>
                        <li>Martin Lawrence</li>
                        <li>Martin Lawrence</li>
                        <li>Martin Lawrence</li>
                      </ul>
                    </div>

                    <div className="votes">
                      <Button>Thumbs down</Button>
                      <Alert variant="light" style={{ marginBottom: 0 }}>0</Alert>
                      <Button>Thumbs up</Button>

                    </div>

                  </li>

                */
              ) : (
                  <span>No results</span>
                )
            }
          </div>
        </Col>
      </Row>
    </Container >
  )
}

export default App;

/*
          {
            props.loggedIn ? (
              <p>You're logged in as {props.facade.getRole()}!</p>
            ) : (
                <p>You're not logged in!</p>

              )

          }
*/
