import React, { useState, useEffect } from 'react';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';


const AddMovie = (props) => {
  const [movie, setMovie] = useState({
    "directors": [{}],
    "actors": [{}],
    "genres": [{}]
  });

  const onChange = evt => {
    const target = evt.target;
    if (target.tagName === "SELECT" && target.id !== "year") {
      const options = evt.target.options;
      let selected = [];
      for (let i = 0; i < options.length; i++) {
        if (options[i].selected) {
          selected.push({ "name": options[i].value })
        }
      }
      movie[target.id] = selected;
      setMovie({ ...movie })
    } else {
      movie[target.id] = target.value;
      setMovie({ ...movie });
    }
  }

  const onSubmit = evt => {
    evt.preventDefault();
    console.log(movie);
  }



  return (
    <Form>



      <Form.Group controlId="title">
        <Form.Label>What is the title of the movie?</Form.Label>
        <Form.Control type="text" placeholder="Bad Boys" />
      </Form.Group>

      <Form.Group controlId="year">
        <Form.Label>What year was it/will it get published</Form.Label>
        <Form.Control as="select">

          {
            props.test.map((t, i) => (
              <option key={i} value={t}>{t}</option>
            ))
          }

        </Form.Control>
      </Form.Group>

      <Form.Group controlId="directors">
        <Form.Label>Choose the directors</Form.Label>
        <Form.Control as="select" multiple>
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
        </Form.Control>
      </Form.Group>

      <Form.Group controlId="actors">
        <Form.Label>Choose the actors</Form.Label>
        <Form.Control as="select" multiple>
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
        </Form.Control>
      </Form.Group>

      <Form.Group controlId="genres">
        <Form.Label>Choose the genres</Form.Label>
        <Form.Control as="select" multiple>
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
        </Form.Control>
      </Form.Group>

      <Button>Add movie</Button>




    </Form>

  );
}

export default AddMovie;
