import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Switch, NavLink, Redirect } from 'react-router-dom';
import "./App.css";
import facade from "./apiFacade";
import LogIn from "./components/LogIn";

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';


function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  const login = (user, pass) => {
    facade
      .login(user, pass)
      .then(res => setLoggedIn(true))
      .catch(err => console.log("wrong credentials"));
  };
  const logout = () => {
    facade.logout();
    setLoggedIn(false);
  };
  return (
    <div>
      <Router>
        <Header loggedIn={loggedIn} logout={logout} facade={facade} />
        <Switch>
          <Route exact path="/">
            <Welcome loggedIn={loggedIn} facade={facade} />
          </Route>
          <Route path="/login">
            {!loggedIn ? (
              <LogIn login={login} />
            ) : (
                <Redirect to="/" />
              )
            }
          </Route>
        </Switch>
      </Router>

    </div>
  );
}

const Header = (props) => {
  console.log(props.facade.getRole())
  return (
    <ul className="menu">
      <li><span className="logo">MovieRev</span></li>
      <li><NavLink to="/" exact activeClassName="active">Home</NavLink></li>
      {
        props.loggedIn && props.facade.getRole() === "admin" ? (
          <li><NavLink to="/admin" activeClassName="active">Admin</NavLink></li>
        ) : null
      }

      {
        props.loggedIn ? (
          <li><NavLink to="/login" activeClassName="active" onClick={props.logout}>Logout</NavLink></li>
        ) : (
            <li><NavLink to="/login" activeClassName="active">Login</NavLink></li>

          )
      }
    </ul>
  );
}

const Welcome = (props) => {
  const [search, setSearch] = useState({});
  const onChange = evt => {
    const target = evt.target;
    if (target.tagName === "SELECT") {
      search['searchBy'] = target.value;
      setSearch({ ...search });
    }
  }

  return (
    <Container>
      <Row className="d-flex justify-content-center">

        <Col className="d-flex default-box" style={{ flexDirection: "column" }} md="7">
          <div className="box-header">Home</div>
          <div>
            <p>
              Feel free to search for any movie by movie title, director, actor or genre.
            </p>
            <Form onChange={onChange}>
              <InputGroup className="mb-3">

                <Form.Control
                  id="searchInput"
                  placeholder="Please to what you want to search by"
                  aria-label="search-input"
                />
                <InputGroup.Append>
                  <Form.Control as="select" style={{ borderTopLeftRadius: "0", borderBottomLeftRadius: "0" }}>
                    <option value="getbytitle">Title</option>
                    <option value="getbydirector">Director</option>
                    <option value="getbyactor">Actor</option>
                    <option value="getbygenre">Genre</option>
                  </Form.Control>
                </InputGroup.Append>
              </InputGroup>
            </Form>
          </div>
        </Col>
      </Row>
    </Container>
  )
}

export default App;

